var contact=document.querySelector(".btn-contact"),popup=document.querySelector(".modal"),close=document.querySelector(".modal-close"),form=popup.querySelector("form"),login=popup.querySelector("[name=login]"),email=popup.querySelector("[name=email]"),storage=localStorage.getItem("login")
